                                :(<Link className='nav-link' to='/auth'> 
                                SIGN IN
                                </Link>
                            )
                        }
                        <CartIcon />

                    </div>
                    {isCartOpen && <CartDropdown />}
                </div>
                <Outlet />
        </Fragment>
        );
    };

  export default Navigation;
